mapper.update:
update admin_info
set name = #{name,jdbcType=VARCHAR},
  sex = #{sex,jdbcType=CHAR},
  username = #{username,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, sex, username, password
from admin_info
where id = #{id,jdbcType=INTEGER}
